CrudVue.vue

<template>
    <h1>Hallo</h1>
    <div>
        <h2>Add Users</h2>
        <from @submit.prevent="postItem">
            <input type="hidden" v-model="form.id" placeholder="Id_Input " />
            <input type="text" v-model="form.name" placeholder="Name " />
            <button type="submit">Add</button>
        </from>

        <h2>List Users</h2>
        <!-- Table -->
        <table>
            <thead>
            <tr>
                <th>ID</th>
                <th>Name</th>
            </tr>
            </thead>
            <tbody>
                <tr v-for="user in users" :key="user.id">
                    <td>{{ user.id }}</td>
                    <td>{{ user.name }}</td>
                </tr>
            </tbody>
        </table>
    </div>
</template>

<script>
import axios from 'axios';
    export default {
        name: 'MyMain',
        data() {
            return {
                form:{
                    id:"",
                    name:"",
                },
                users:[],
            }
        },
        
        methods:{
            getItem(){
                axios.get("http://localhost:3000/users")
                .then((response) => {
                    this.users = response.data;
                    console.log("data", response.data);
                })
                .catch((error) => {
                    console.log("Error Get Data ", error);
                })
            },
            postItem(){
                axios
                .post("http://localhost:3000/users", this.form)
                .then(() => {
                    this.getItem();
                    this.form.id= "";
                    this.form.name= "";
                    alert("Data berhasil dipost");
                })
                .catch((error) => {
                    console.log("Error Get Data ", error);
                })
            }
        },
        
        mounted() {
            this.getItem();
        }
    }


</script>

<style>
    .tabelnya {
        width: 100%;
        align-items: ClientRequest;
    }
</style>
